blockMenu.onMenuOptionSelected(function (option, index) {
    if (option == "Play") {
        Is_Title_Screen = 0
        blockMenu.closeMenu()
        GamePause = 0
        tiles.setTilemap(tilemap`level2`)
        Level1 = sprites.create(assets.image`Level1`, SpriteKind.Level1)
        tiles.placeOnTile(Level1, tiles.getTileLocation(0, 1))
        Cursor = sprites.create(assets.image`myImage`, SpriteKind.Cursor)
        controller.moveSprite(Cursor)
        blockMenu.setControlsEnabled(false)
        scene.cameraFollowSprite(Cursor)
        Player1 = sprites.create(assets.image`Me`, SpriteKind.Player)
        Player2 = sprites.create(assets.image`Insert Name Here`, SpriteKind.Player2)
        Player1.setFlag(SpriteFlag.Invisible, true)
        Player2.setFlag(SpriteFlag.Invisible, true)
    } else if (option == "Controls") {
        game.showLongText("To move, use arrow keys left up down right, to jump. Use the A button. On pc it is space or z. B to access menu.", DialogLayout.Center)
    } else if (option == "Creators") {
        game.showLongText("thesonicfan192: Lead Developer", DialogLayout.Center)
        game.showLongText("ThunderDrop180: Made a now deleted github", DialogLayout.Center)
    } else if (option == "About") {
        game.showLongText("Map is inspired by the winner of ocean game jam, three brave cats, aswell as story. By the way, I recommend you read this. I might put a secret in here. Also, make sure to connect plaer 2.", DialogLayout.Center)
        game.showLongText("Want to become a contributor? While this game is in beta! You can become one! I might be generous and give out contributor for 1 dollar donations on itch.io", DialogLayout.Center)
    } else if (option == "Update Logs") {
        game.showLongText("Update Log: v0.8 It is still in beta, but improved the menus. Hooray!", DialogLayout.Center)
    } else if (option == "Continue") {
        blockMenu.setControlsEnabled(false)
        blockMenu.closeMenu()
        GamePause = 0
        if (Game_Start == 1) {
            controller.moveSprite(Player1, 100, 0)
            controller.player2.moveSprite(Player2, 100, 0)
        } else {
            controller.moveSprite(Cursor)
        }
    } else if (option == "Store") {
        blockMenu.closeMenu()
        blockMenu.showMenu(["Broken Jump: 10000 score", "More coming soon"], MenuStyle.Grid, MenuLocation.FullScreen)
    } else if (option == "Broken Jump: 10000 score") {
        if (info.score() == 10000) {
            BrokenJump = 1
            game.showLongText("Thank you for your purchase :) this confirms you got the item", DialogLayout.Center)
            info.changeScoreBy(-10000)
        } else {
        	
        }
    } else {
    	
    }
})
